import React, { Component } from "react";
import {MDBContainer} from 'mdbreact'
import './header.css'
class NavbarPage extends Component {

  state = {
    links: [
      {
        id: 1,
        name: "Home",
        href: '#Home',
        className: "side_nav_item"
      },
      {
        id: 2,
        name: "About",
        href: "#About",
        className: "side_nav_item"
      },
      {
        id: 3,
        name: "Skills",
        href: "#Skills",
        className: "side_nav_item"
      },
      {
        id: 4,
        name: "Timline",
        href: "#Timline",
        className: "side_nav_item"
      },

      {
        id: 5,
        name: "Portfolio",
        href: "#Portfolio",
        className: "side_nav_item"
      },

      {
        id: 6,
        name: "Contact",
        href: "#Contact",
        className: "side_nav_item"
      },
    ],
    activeLink: null
  };

  handleClick = id => {
    this.setState({ activeLink: id });
  };
  render() {
    const { links, activeLink } = this.state;
    return (
      <div className='nav' >

      <MDBContainer>
        <nav>
          <div>
            <h1 className='title'><span className='titleColor'>K</span>enan</h1>
          </div>
          <ul>
          {links.map(link => {
          return (
                <li key={link.id}
                  onClick={() => this.handleClick(link.id)}
                  className={
                    link.className +
                    (link.id === activeLink ? " active_item" : "")
                  }
                >
                  {link.name} {link.id === activeLink && side_nav_item_active}
                </li>
          );
        })}
          </ul>
        </nav>

      </MDBContainer>
      </div>

    );
  }
}

export default NavbarPage;
