import React from "react";
import { MDBCol, MDBRow } from "mdbreact";

const infoData = [
    {
        title: 'Name:',
        text: "Boyukkishiyev Kanan",
    },

    {
        title: 'Age:',
        text: '22',
    },

    {
        title: 'Phone:',
        text: '+994554562007',
    },

    {
        title: 'Address:',
        text: 'Baku, Azerbayjan',
    },

    {
        title: 'Experience',
        text: '2 Years',
    },

    {
        title: 'Freelance',
        text: 'Available',
    },

]

const InfoItem = ({ data }) => (
    <MDBCol sm='6'>
        <p><span>{data.title} </span>{data.text}</p>
    </MDBCol>
)


const Info = () =>
    infoData.length > 0 && (
        <MDBCol xl="7" lg="6">
            <div class="about-info">
                <div class="main-title">
                    <h3>Discover</h3>
                    <h2>About Me</h2>
                </div>
                <p>My name is Razan Smith. I'm a Freelance Full Stack Developer based in New York, USA, and I'm very passionate and dedicated to my work .With 6 years experience as a professional Full Stack Developer, I have acquired the skills necessary to build great and premium websites.</p>
                <div class="personal-info">
                    <MDBRow>
                        {infoData.map((data, idx) => (
                            <InfoItem data={data} key={idx} />
                        ))}
                    </MDBRow>
                </div>
                <a to="#" class="main-btn">Download CV</a>
            </div>
        </MDBCol>
    );

export default Info;
