import React from 'react';
import { MDBCol, MDBRow, MDBContainer, } from "mdbreact";
import './contact.css'

const ContactData = [
    {
        ContactTitle: 'Baku,  Azerbayjan',
        ContactIcon: "fas fa-map-marker-alt fa-2x",
    },

    {
        ContactTitle: '+994554562007',
        ContactIcon: "fas fa-phone fa-2x",
    },

    {
        ContactTitle: 'boyukkishiyev@gmail.com:',
        ContactIcon: 'fas fa-envelope fa-2x',
    },

    
]

const inputdata = [
    {
        inputLable: "Your name",
        inputType: 'text',
        inputId: 'message'

    },

    {
        inputLable: "Your email",
        inputType: 'email',
        inputId: 'email'
    },

    {
            inputLable: "Subject",
            inputType: 'text',
            inputId: 'subject'
    },

    {
            inputLable: "Your message",
            inputType: 'text',
            inputId: 'message'
    },
]

const ContactItem = ({ data }) => (
    <li className='detail-List mb-5'>
        <p>{data.ContactTitle}</p>
        <i className={`${data.ContactIcon}`}></i>
    </li>
);

const InputItem = ({ data }) => (
    <MDBCol md='12'>
        <div className="md-form mb-3">
            <input type={`${data.inputType}`} id={`${data.inputId}`} name={`${data.inputId}`} class="form-control" />
            <label for={`${data.inputId}`} class="">{data.inputLable}</label>
        </div>
    </MDBCol>
);
const contoct = () =>

    ContactData.length > 0 && (
        <section className=" skills " style={{ backgroundColor: '#24283d' }}>

            <MDBContainer>
                <div class="main-title text-center mb-5">
                    <h2>Contoct Me</h2>
                </div>
                <MDBRow>
                    <MDBCol md='4' className="text-center">
                        <ul style={{ display: 'block' }} className="list-unstyled  mb-0">
                            {inputdata.map((data, idx) => (
                                <ContactItem data={data} key={idx} />
                            ))}
                        </ul>
                    </MDBCol>

                    <MDBCol md='8' className=" mb-md-0 mb-5">
                        <form id="contact-form" name="contact-form" action="mail.php" method="POST">

                            <MDBRow>
                                {ContactData.map((data, idx) => (
                                    <inputItem data={data} key={idx} />
                                ))}
                            </MDBRow>
                        </form>

                        <div className="text-center text-md-left">
                            <a className="btn btn-primary" onclick="document.getElementById('contact-form').submit();" href='#!'>Send</a>
                        </div>
                        <div className="status"></div>
                    </MDBCol>


                </MDBRow>
            </MDBContainer>
        </section>
    )


export default contoct;
