import React, { Component } from "react";
import { MDBCol, MDBRow, MDBContainer, MDBMask, MDBView, } from "mdbreact";
import './portfolio.css';

class TabsDefault extends Component {
    state = {
        activeItem: "1"
    };

    toggle = tab => e => {
        if (this.state.activeItem !== tab) {
            this.setState({
                activeItem: tab
            });
        }
    };

    render() {
        return (
            <>
                <div className="tabs" style={{ backgroundColor: "#24283d" }}>
                    <MDBContainer>

                        <Tabs>
                            <Tab label="all">
                                <MDBRow>
                                    <MDBCol xl='4' lg='4' md='6' sm='6'>
                                        <MDBView hover>
                                            <img 
                                            width='100%'
                                                src="data:image/png;base64,"
                                                className="img-fluid"
                                                alt=""
                                            />
                                            <MDBMask className="" overlay="black-strong">
                                                <h1 className="white-text">Jobustan</h1>
                                                <div>
                                                    <p>web</p>
                                                </div>
                                            </MDBMask>
                                        </MDBView>
                                    </MDBCol>
                                    <MDBCol xl='4' lg='4' md='6' sm='6'>
                                        <MDBView hover>
                                            <img
                                                src="https://mdbootstrap.com/img/Others/documentation/forest-sm-mini.jpg"
                                                className="img-fluid"
                                                alt=""
                                            />
                                        </MDBView>                                    </MDBCol>
                                    <MDBCol xl='4' lg='4' md='6' sm='6'>
                                        <MDBView hover>
                                            <img
                                                src="https://mdbootstrap.com/img/Others/documentation/forest-sm-mini.jpg"
                                                className="img-fluid"
                                                alt=""
                                            />
                                        </MDBView>                                    </MDBCol>
                                    <MDBCol xl='4' lg='4' md='6' sm='6'>
                                        <MDBView hover>
                                            <img
                                                src="https://mdbootstrap.com/img/Others/documentation/forest-sm-mini.jpg"
                                                className="img-fluid"
                                                alt=""
                                            />
                                        </MDBView>                                    </MDBCol>
                                    <MDBCol xl='4' lg='4' md='6' sm='6'>
                                        <MDBView hover>
                                            <img
                                                src="https://mdbootstrap.com/img/Others/documentation/forest-sm-mini.jpg"
                                                className="img-fluid"
                                                alt=""
                                            />
                                        </MDBView>                                    </MDBCol>
                                    <MDBCol xl='4' lg='4' md='6' sm='6'>
                                        <MDBView hover>
                                            <img
                                                src="https://mdbootstrap.com/img/Others/documentation/forest-sm-mini.jpg"
                                                className="img-fluid"
                                                alt=""
                                            />
                                        </MDBView>                                    </MDBCol>
                                </MDBRow>
                            </Tab>
                            <Tab label="Web">
                                <div>
                                    <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ2E3SrDqYWF9J_TDWNzjZkwJOAGxkLJVZPvUly7SqUL9WqLqEw" />
                                </div>
                            </Tab>

                        </Tabs>
                    </MDBContainer>
                </div>
            </>
        )
    }

}
export default TabsDefault;


class Tabs extends React.Component {
    state = {
        activeTab: this.props.children[0].props.label
    }
    changeTab = (tab) => {

        this.setState({ activeTab: tab });
    };
    render() {

        let content;
        let buttons = [];
        return (
            <div>
                {React.Children.map(this.props.children, child => {
                    buttons.push(child.props.label)
                    if (child.props.label === this.state.activeTab) content = child.props.children
                })}

                <TabButtons activeTab={this.state.activeTab} buttons={buttons} changeTab={this.changeTab} />
                <div className="tab-content">{content}</div>

            </div>
        );
    }
}

const TabButtons = ({ buttons, changeTab, activeTab }) => {

    return (
        <div className="tab-buttons">
            {buttons.map(button => {
                return <button className={button === activeTab ? 'active' : ''} onClick={() => changeTab(button)}>{button}</button>
            })}
        </div>
    )
}

const Tab = props => {
    return (
        <React.Fragment>
            {props.children}
        </React.Fragment>
    )
}
