import React from "react";
import { MDBCol, MDBRow } from "mdbreact";

const infoData = [
    {
        title: 'Name:',
        text: "Boyukkishiyev Kanan",
    },

    {
        text: 'Age:',
        text: '22',
    },

    {
        text: 'Phone:',
        text: '+994554562007',
    },

    {
        text: 'Address:',
        text: 'Baku, Azerbayjan',
    },

    {
        text: 'Experience',
        text: '2 Years',
    },

    {
        text: 'Freelance',
        text: 'Available',
    },

]

const InfoItem = ({ data }) => (
    <MDBCol sm='6'>
        <p><span>{data.title} </span>{data.text}</p>
    </MDBCol>
)


const Info = () => {
    return (
        <MDBCol xl="7" lg="6">
            <div class="about-info">
                <div class="main-title">
                    <h3>Discover</h3>
                    <h2>About Me</h2>
                </div>
                <p>My name is Razan Smith. I'm a Freelance Full Stack Developer based in New York, USA, and I'm very passionate and dedicated to my work .With 6 years experience as a professional Full Stack Developer, I have acquired the skills necessary to build great and premium websites.</p>
                <div class="personal-info">
                    <MDBRow>
                        <MDBCol sm='6'>
                            <p><span>Name: </span>Boyukkishiyev Kanan</p>
                        </MDBCol>
                        <MDBCol sm='6'>
                            <p><span>Age: </span>22</p>
                        </MDBCol>
                        <MDBCol sm='6'>
                            <p><span>Phone: </span>+994554562007</p>
                        </MDBCol>
                        <MDBCol sm='6'>
                            <p><span>Address: </span>Baku, Azerbayjan</p>
                        </MDBCol>
                        <MDBCol sm='6'>
                            <p><span>Experience: </span>2 Years</p>
                        </MDBCol>
                        <MDBCol sm='6'>
                            <p><span>Freelance: </span>Available</p>
                        </MDBCol>
                    </MDBRow>
                </div>
                <a href="#" class="main-btn">Download CV</a>
            </div>
        </MDBCol>
    );
}

export default Info;
