import React, { Component } from "react";
import { MDBRow, MDBContainer } from "mdbreact";
import './portfolio.css';
import TabsRow from './Tab/tab';
class TabsDefault extends Component {
    state = {
        activeItem: "1"
    };

    toggle = tab => e => {
        if (this.state.activeItem !== tab) {
            this.setState({
                activeItem: tab
            });
        }
    };

    render() {
        return (
            <>
                <div className="tabs" id="Portfolio" style={{ backgroundColor: "#24283d" }}>
                    <MDBContainer>
                        <Tabs>
                            
                            <Tab label="all">

                                <MDBRow>
                                    <TabsRow img="https://static.toiimg.com/photo/72975551.cms" title='Job' link='https://www.jobustan.com' />
                                    <TabsRow img="https://static.toiimg.com/photo/72975551.cms" title='Job2' link='https://www.jobustan.com' />
                                </MDBRow>

                            </Tab>

                            <Tab label="Web">

                                <MDBRow>
                                    <TabsRow img="https://static.toiimg.com/photo/72975551.cms" title='Job3' link='https://www.jobustan.com' />
                                    <TabsRow img="https://static.toiimg.com/photo/72975551.cms" title='Job4' link='https://www.jobustan.com' />
                                </MDBRow>

                            </Tab>

                        </Tabs>
                    </MDBContainer>
                </div>
            </>
        )
    }
}
export default TabsDefault;


class Tabs extends React.Component {
    state = {
        activeTab: this.props.children[0].props.label
    }
    changeTab = (tab) => {

        this.setState({ activeTab: tab });
    };
    render() {

        let content;
        let buttons = [];
        return (
            <div>
                {React.Children.map(this.props.children, child => {
                    buttons.push(child.props.label)
                    if (child.props.label === this.state.activeTab) content = child.props.children
                })}

                <TabButtons activeTab={this.state.activeTab} buttons={buttons} changeTab={this.changeTab} />
                <div className="tab-content">{content}</div>

            </div>
        );
    }
}

const TabButtons = ({ buttons, changeTab, activeTab }) => {

    return (
        <div className="tab-buttons">
            {buttons.map(button => {
                return <button className={button === activeTab ? 'active' : ''} onClick={() => changeTab(button)}>{button}</button>
            })}
        </div>
    )
}

const Tab = props => {
    return (
        <React.Fragment>
            {props.children}
        </React.Fragment>
    )
}
